trd2 = Season.create!(number: 2, air_date: '2011-10-16', serie_id: trd.id)
trd3 = Season.create!(number: 3, air_date: '2012-10-14', serie_id: trd.id)
trd4 = Season.create!(number: 4, air_date: '2013-10-13', serie_id: trd.id)
trd5 = Season.create!(number: 5, air_date: '2014-10-12', serie_id: trd.id)
trd6 = Season.create!(number: 6, air_date: '2015-10-11', serie_id: trd.id)
